// Get current holdings from broker/database
// This is a placeholder - replace with actual broker API call
const currentHoldings = [
  { ticker: "RELIANCE.NS", quantity: 10, marketValue: 13849 },
  { ticker: "TCS.NS", quantity: 5, marketValue: 18500 },
  { ticker: "INFY.NS", quantity: 8, marketValue: 14400 }
];

return [{ json: { holdings: currentHoldings } }];

// Portfolio Rebalancing Logic
const strategyData = $input.all()[0].json;
const currentHoldings = $input.all()[1].json.holdings;

const targetPortfolio = strategyData.top || [];
const summary = strategyData.summary || {};

// Create maps for easier lookup
const currentMap = new Map();
currentHoldings.forEach(holding => {
  currentMap.set(holding.ticker, {
    currentQty: holding.quantity || 0,
    currentValue: holding.marketValue || 0
  });
});

const rebalanceOrders = [];
const timestamp = new Date().toISOString();

// Generate buy/sell orders for target portfolio
targetPortfolio.forEach(target => {
  const ticker = target.Ticker;
  const targetQty = target.Qty || 0;
  const current = currentMap.get(ticker) || { currentQty: 0, currentValue: 0 };
  const qtyDiff = targetQty - current.currentQty;

  if (Math.abs(qtyDiff) > 0) {
    rebalanceOrders.push({
      ticker: ticker,
      action: qtyDiff > 0 ? 'BUY' : 'SELL',
      quantity: Math.abs(qtyDiff),
      targetQty: targetQty,
      currentQty: current.currentQty,
      currentPrice: target.Price || 0,
      estimatedValue: Math.abs(qtyDiff) * (target.Price || 0),
      priority: target.Rank || 999,
      momentumScore: target.MomentumScore || 0,
      allocation: target['Allocation(%)'] || 0,
      orderType: 'MARKET',
      validity: 'DAY',
      product: 'CNC'
    });
  }

  // Remove from current map (processed)
  currentMap.delete(ticker);
});

// Handle positions to exit (not in target portfolio)
currentMap.forEach((current, ticker) => {
  if (current.currentQty > 0) {
    rebalanceOrders.push({
      ticker: ticker,
      action: 'SELL',
      quantity: current.currentQty,
      targetQty: 0,
      currentQty: current.currentQty,
      estimatedValue: current.currentValue,
      priority: 1, // High priority for exits
      reason: 'EXIT_POSITION',
      orderType: 'MARKET',
      validity: 'DAY',
      product: 'CNC'
    });
  }
});

// Sort orders: Sells first, then buys by priority
rebalanceOrders.sort((a, b) => {
  if (a.action === 'SELL' && b.action === 'BUY') return -1;
  if (a.action === 'BUY' && b.action === 'SELL') return 1;
  return a.priority - b.priority;
});

// Calculate rebalancing metrics
const totalOrderValue = rebalanceOrders.reduce((sum, order) => 
  sum + (order.estimatedValue || 0), 0);

const buyOrders = rebalanceOrders.filter(o => o.action === 'BUY');
const sellOrders = rebalanceOrders.filter(o => o.action === 'SELL');

const rebalanceResult = {
  timestamp: timestamp,
  date: timestamp.split('T')[0],
  strategy: {
    totalCapital: summary.total_capital || 0,
    investedAmount: summary.invested_amount || 0,
    utilization: summary.utilization_pct || 0,
    selectedStocks: summary.selected_stocks || 0,
    eligibleStocks: summary.eligible_stocks || 0,
    avgMomentumScore: summary.avg_momentum_score || 0
  },
  rebalancing: {
    totalOrders: rebalanceOrders.length,
    buyOrders: buyOrders.length,
    sellOrders: sellOrders.length,
    totalOrderValue: totalOrderValue,
    buyValue: buyOrders.reduce((sum, o) => sum + (o.estimatedValue || 0), 0),
    sellValue: sellOrders.reduce((sum, o) => sum + (o.estimatedValue || 0), 0)
  },
  orders: rebalanceOrders,
  targetPortfolio: targetPortfolio,
  currentHoldings: currentHoldings
};

return [{ json: rebalanceResult }];

